const url = `https://api-inference.huggingface.co/models/${process.env.HF_MODEL || '{{model}}'}`;
const res = await fetch(url, { method: 'POST', headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${process.env.HF_TOKEN}` }, body: JSON.stringify({ inputs: 'Say hello' }) });
const json = await res.json();
console.log(JSON.stringify(json));
