const i18next = require('i18next');
(async () => {
  await i18next.init({ lng: 'en', resources: { en: { translation: { hello: 'Hello' } }, ja: { translation: { hello: 'こんにちは' } } } });
  console.log('EN:', i18next.t('hello'));
  await i18next.changeLanguage('ja');
  console.log('JA:', i18next.t('hello'));
})();
