import { InfluxDB, Point } from '@influxdata/influxdb-client';
const db = new InfluxDB({ url: process.env.INFLUX_URL, token: process.env.INFLUX_TOKEN });
const w = db.getWriteApi(process.env.INFLUX_ORG, process.env.INFLUX_BUCKET);
w.writePoint(new Point('cpu').floatField('usage', 0.5));
await w.close();
console.log('written');
