import { Version3Client } from 'jira.js';
const client = new Version3Client({ host: process.env.JIRA_HOST, authentication: { basic: { email: process.env.JIRA_EMAIL, apiToken: process.env.JIRA_TOKEN } } });
const res = await client.issueSearch.searchForIssuesUsingJql({ jql: 'ORDER BY created DESC', maxResults: 1 });
console.log(res.total >= 0);
