const express = require('express');
const Joi = require('joi');
const app = express();
app.use(express.json());
app.post('/echo', (req,res)=>{ const schema = Joi.object({ name: Joi.string().required() }); const { error, value } = schema.validate(req.body); if (error) return res.status(400).json({ error: error.message }); res.json({ name: value.name }); });
app.listen(Number('{{port}}'), ()=>console.log('listening on {{port}}'));
