const { Kafka } = require('kafkajs');
const kafka = new Kafka({ clientId: 'app', brokers: (process.env.KAFKA_BROKERS||'localhost:9092').split(',') });
(async()=>{ const admin = kafka.admin(); await admin.connect(); await admin.createTopics({ topics: [{ topic: process.env.KAFKA_TOPIC || '{{topic}}' }] }); await admin.disconnect(); })();
