const { Kafka } = require('kafkajs');
const kafka = new Kafka({ clientId: 'app', brokers: (process.env.KAFKA_BROKERS||'localhost:9092').split(',') });
(async()=>{ const c = kafka.consumer({ groupId: 'g1' }); await c.connect(); await c.subscribe({ topic: process.env.KAFKA_TOPIC||'{{topic}}', fromBeginning: true }); await c.run({ eachMessage: async ({ message })=> console.log(message.value?.toString()) }); })();
