const { Kafka } = require('kafkajs');
const kafka = new Kafka({ clientId: 'app', brokers: (process.env.KAFKA_BROKERS||'localhost:9092').split(',') });
(async () => {
  const p = kafka.producer();
  await p.connect();
  await p.send({ topic: process.env.KAFKA_TOPIC || '{{topic}}', messages: [{ value: 'hello' }] });
  await p.disconnect();
  console.log('sent');
})();
