const { Kafka } = require('kafkajs');
const kafka = new Kafka({ clientId: 'app', brokers: (process.env.BROKERS||'localhost:9092').split(',') });
(async()=>{ const p = kafka.producer({ transactionalId: 'tx1' }); await p.connect(); const tx = await p.transaction(); await tx.send({ topic: process.env.TOPIC||'{{topic}}', messages: [{ value: 'hello' }] }); await tx.commit(); await p.disconnect(); })();
