const { Kafka } = require('kafkajs');
const kafka = new Kafka({ clientId: 'demo', brokers: ['localhost:9092'] });
const consumer = kafka.consumer({ groupId: 'g1' });
(async () => { await consumer.connect(); await consumer.subscribe({ topic: 'events', fromBeginning: true }); await consumer.run({ eachBatch: async ({ batch, resolveOffset, heartbeat }) => { for (const msg of batch.messages) { try { console.log('msg', msg.value?.toString()); resolveOffset(msg.offset); await heartbeat(); } catch(e){ console.error('retry needed'); } } } }); })();
