const { Kafka } = require('kafkajs');
const kafka = new Kafka({ clientId: 'demo', brokers: ['localhost:9092'] });
const consumer = kafka.consumer({ groupId: 'g1' });
(async ()=>{ await consumer.connect(); await consumer.subscribe({ topic: 'events', fromBeginning: true }); await consumer.run({ autoCommit: false, eachMessage: async ({ topic, partition, message, heartbeat, pause }) => { console.log('msg', message.value?.toString()); await consumer.commitOffsets([{ topic, partition, offset: (Number(message.offset)+1).toString() }]); await heartbeat(); } }); })();
