const Koa = require('koa');
const app = new Koa();
app.use(async (ctx, next) => { if (ctx.path === '/health') { ctx.body = { status: 'ok' }; return; } await next(); });
app.use(async (ctx) => { ctx.body = { hello: 'world' }; });
app.listen({{port}}, () => console.log('listening on {{port}}'));
