files.template:
apiVersion: apps/v1
kind: DaemonSet
metadata: { name: {{name}} }
spec: { selector: { matchLabels: { app: {{name}} } }, template: { metadata: { labels: { app: {{name}} } }, spec: { containers: [ { name: agent, image: busybox, args: ['sh','-c','while true; do sleep 60; done'] } ] } } }
