import { Kysely, SqliteDialect } from 'kysely';
import Database from 'better-sqlite3';
const db = new Kysely({ dialect: new SqliteDialect({ database: new Database('app.db') }) });
await db.executeQuery({ sql: 'CREATE TABLE IF NOT EXISTS items (id integer primary key, name text)', parameters: [] });
await db.executeQuery({ sql: `INSERT INTO items(name) VALUES('hello')`, parameters: [] });
const r = await db.executeQuery({ sql: 'SELECT count(*) c FROM items', parameters: [] });
console.log(r.rows);
process.exit(0);
