import { connect } from '@lancedb/lancedb';
const db = await connect('./.lancedb');
const table = await db.createTable('docs', [{ vector: [0.1,0.2,0.3], text: 'hello', tag: 'a' }, { vector: [0.2,0.1,0.25], text: 'fluorite', tag: 'b' }]);
const res = await table.search([0.1,0.2,0.3]).where('tag = 'a'').limit(2).toArray();
console.log(res.map(r => r.text));
