import weaviate from 'weaviate-ts-client';
import { WeaviateStore } from '@langchain/weaviate';
import { OpenAIEmbeddings } from '@langchain/openai';
import { CohereClient } from 'cohere-ai';
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
const embeddings = new OpenAIEmbeddings({ apiKey: process.env.OPENAI_API_KEY });
const store = await WeaviateStore.fromTexts(['hello world', 'fluorite mcp spike'], [{ id: '1' }, { id: '2' }], embeddings, { client, indexName: process.env.WEAVIATE_CLASS || '{{class_name}}' });
const query = 'fluorite';
const initial = await store.similaritySearch(query, 2);
const cohere = new CohereClient({ token: process.env.COHERE_API_KEY });
const reranked = await cohere.rerank({ model: '{{cohere_rerank_model}}', query, documents: initial.map(d => d.pageContent) });
console.log(reranked.results?.map(r => ({ idx: r.index, score: r.relevance_score })));
