import { ChatOpenAI, OpenAIEmbeddings } from '@langchain/openai';
import { MultiQueryRetriever } from 'langchain/retrievers/multi_query';
import { MemoryVectorStore } from 'langchain/vectorstores/memory';
const docs = [{ pageContent: 'hello world' }, { pageContent: 'fluorite mcp spike' }];
const embeddings = new OpenAIEmbeddings({ apiKey: process.env.OPENAI_API_KEY });
const store = await MemoryVectorStore.fromDocuments(docs, embeddings);
const llm = new ChatOpenAI({ apiKey: process.env.OPENAI_API_KEY });
const retriever = MultiQueryRetriever.fromLLM({ llm, retriever: store.asRetriever(2) });
const results = await retriever.getRelevantDocuments('fluorite');
console.log(results.map(r => r.pageContent));
