import { ChatOpenAI, OpenAIEmbeddings } from '@langchain/openai';
import { MemoryVectorStore } from 'langchain/vectorstores/memory';
const docs = [{ pageContent: 'fluorite mcp provides spike templates' }, { pageContent: 'vector databases enable semantic search' }];
const embeddings = new OpenAIEmbeddings({ apiKey: process.env.OPENAI_API_KEY });
const store = await MemoryVectorStore.fromDocuments(docs, embeddings);
const query = 'What does fluorite mcp provide?';
const retrieved = await store.similaritySearch(query, 2);
const context = retrieved.map(d=>d.pageContent).join('
');
const llm = new ChatOpenAI({ apiKey: process.env.OPENAI_API_KEY });
const res = await llm.invoke(`Use the context to answer succinctly.
Context:
${context}

Question: ${query}`);
console.log(res.content);
