import { ChatOpenAI } from '@langchain/openai';
import { RunnableSequence } from 'langchain/schema/runnable';
const llm = new ChatOpenAI({ apiKey: process.env.OPENAI_API_KEY });
const route = (q)=> q.includes('time') ? 'time' : 'general';
const chain = RunnableSequence.from([async (q)=>({ prompt: route(q)==='time' ? `What time is it?` : `Answer: ${q}` }), llm]);
const res = await chain.invoke('Say hello');
console.log(res.content);
