import weaviate from 'weaviate-ts-client';
import { SelfQueryRetriever } from 'langchain/retrievers/self_query';
import { WeaviateStore } from '@langchain/weaviate';
import { OpenAIEmbeddings, ChatOpenAI } from '@langchain/openai';
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
const embeddings = new OpenAIEmbeddings({ apiKey: process.env.OPENAI_API_KEY });
const store = await WeaviateStore.fromTexts(['hello world', 'fluorite mcp spike'], [{ tag: 'a' }, { tag: 'b' }], embeddings, { client, indexName: process.env.WEAVIATE_CLASS || '{{class_name}}' });
const retriever = SelfQueryRetriever.fromLLM({ llm: new ChatOpenAI({ apiKey: process.env.OPENAI_API_KEY }), vectorStore: store, documentContents: 'text', attributeInfo: [{ name: 'tag', type: 'string', description: 'label' }] });
const res = await retriever.getRelevantDocuments('find docs with tag a');
console.log(res.map(r=>r.pageContent));
