import { CharacterTextSplitter } from 'langchain/text_splitter';
const splitter = new CharacterTextSplitter({ separator: '.', chunkSize: 0, chunkOverlap: 0 });
const text = 'Hello world. Fluorite MCP. Vector search is useful.';
const docs = await splitter.createDocuments([text]);
console.log(docs.map(d=>d.pageContent.trim()));
