process.env.LANGSMITH_TRACING_V2 = 'true';
process.env.LANGSMITH_ENDPOINT = process.env.LANGSMITH_ENDPOINT || 'https://api.smith.langchain.com';
import { ChatOpenAI } from '@langchain/openai';
const llm = new ChatOpenAI({ apiKey: process.env.OPENAI_API_KEY });
const res = await llm.invoke('Say hello');
console.log(res.content);
