import LaunchDarkly from 'launchdarkly-node-server-sdk';
const client = LaunchDarkly.init(process.env.LD_SDK_KEY || 'sdk-key');
await client.waitForInitialization();
const v = await client.variation(process.env.LD_FLAG || '{{flagKey}}', { key: 'user' }, false);
console.log(v);
await client.flush();
await client.close();
