import { GraphQLClient, gql } from 'graphql-request';
const client = new GraphQLClient('https://api.linear.app/graphql', { headers: { Authorization: process.env.LINEAR_API_KEY } });
const q = gql`{ viewer { name } }`;
try { const r = await client.request(q); console.log(!!r.viewer.name); } catch(e){ console.log('Provide valid api key'); }
