const express = require('express');
const crypto = require('crypto');
const app = express();
app.use(express.raw({ type: '*/*' }));
app.post('/linear', (req,res)=>{ const sig = req.headers['x-linear-signature']; const my = crypto.createHmac('sha256', process.env.LINEAR_SECRET).update(req.body).digest('hex'); res.status(sig===my?200:401).send('ok'); });
app.listen(3000, ()=>console.log('listening'));
