import { Settings } from 'llamaindex';
import { CohereClient } from 'cohere-ai';
Settings.llm = undefined;
const cohere = new CohereClient({ token: process.env.COHERE_API_KEY });
const docs = ['hello world', 'fluorite mcp spike', 'vector databases'];
const out = await cohere.rerank({ model: 'rerank-english-v3.0', query: 'fluorite', documents: docs });
console.log(out.results?.map(r => ({ idx: r.index, score: r.relevance_score })));
