import { VectorStoreIndex, SimpleDocument } from 'llamaindex';
const docs = [new SimpleDocument({ text: 'fluorite mcp serves spikes' })];
const index = await VectorStoreIndex.fromDocuments(docs);
const qe = index.asQueryEngine();
const res = await qe.query('What does fluorite mcp serve?');
console.log(String(res));
