import { Settings } from 'llamaindex';
import OpenAI from 'openai';
import weaviate from 'weaviate-ts-client';
Settings.llm = undefined; // Not needed for this minimal vector store example
const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });
const client = weaviate.client({ scheme: process.env.WEAVIATE_SCHEME || '{{weaviate_scheme}}', host: process.env.WEAVIATE_HOST || '{{weaviate_host}}' });
const docs = ['hello world', 'fluorite mcp spike'];
const emb = await openai.embeddings.create({ model: 'text-embedding-3-small', input: docs });
for (let i = 0; i < docs.length; i++) {
  await client.data.creator().withClassName(process.env.WEAVIATE_CLASS || '{{class_name}}').withProperties({ text: docs[i] }).withVector(emb.data[i].embedding).do();
}
const res = await client.graphql.get().withClassName(process.env.WEAVIATE_CLASS || '{{class_name}}').withNearVector({ vector: emb.data[0].embedding }).withLimit(2).do();
console.log(JSON.stringify(res, null, 2));
