import { GraphQLClient, gql } from 'graphql-request';
const client = new GraphQLClient(process.env.MAGENTO_URL || '{{url}}');
const q = gql`{ storeConfig { store_name } }`;
try { const r = await client.request(q); console.log(!!r.storeConfig); } catch(e){ console.log('Provide valid endpoint'); }
