const formData = require('form-data');
const Mailgun = require('mailgun.js');
const mg = new Mailgun(formData).client({ username: 'api', key: process.env.MAILGUN_API_KEY||'' });
(async () => { const r = await mg.messages.create('{{domain}}', { from: 'no-reply@example.com', to: ['user@example.com'], subject: 'Hello', text: 'Hello Mailgun' }); console.log(r.id); })();
