import { MeiliSearch } from 'meilisearch';
const client = new MeiliSearch({ host: process.env.MEILI_URL || 'http://127.0.0.1:7700' });
const idx = await client.getOrCreateIndex(process.env.MEILI_INDEX || '{{index}}');
await idx.addDocuments([{ id: 1, title: 'Hello' }]);
console.log('indexed');
