const { MeiliSearch } = require('meilisearch');
const client = new MeiliSearch({ host: '{{host}}' });
(async () => {
  const index = await client.getOrCreateIndex('movies', { primaryKey: 'id' });
  await index.addDocuments([{ id: 1, title: 'Inception' }]);
  const search = await index.search('incep');
  console.log('hits', search.hits.length);
})();
