import { MilvusClient, DataType } from '@zilliz/milvus2-sdk-node';
const client = new MilvusClient({ address: process.env.MILVUS_ADDRESS || '{{milvus_address}}' });
const name = process.env.MILVUS_COLLECTION || '{{collection_name}}';
await client.createCollection({
  collection_name: name,
  fields: [
    { name: 'id', data_type: DataType.Int64, is_primary_key: true, autoID: true },
    { name: 'text', data_type: DataType.VarChar, max_length: 256 },
    { name: 'embedding', data_type: DataType.FloatVector, type_params: { dim: '3' } }
  ]
});
console.log('Created collection', name);
