import { MilvusClient } from '@zilliz/milvus2-sdk-node';
const client = new MilvusClient({ address: process.env.MILVUS_ADDRESS || '{{milvus_address}}' });
await client.dropCollection({ collection_name: process.env.MILVUS_COLLECTION || '{{collection_name}}' });
console.log('Collection dropped');
