import { MilvusClient, IndexType, MetricType } from '@zilliz/milvus2-sdk-node';
const client = new MilvusClient({ address: process.env.MILVUS_ADDRESS || '{{milvus_address}}' });
const name = process.env.MILVUS_COLLECTION || '{{collection_name}}';
await client.createIndex({ collection_name: name, field_name: 'embedding', index_type: IndexType.IVF_SQ8, metric_type: MetricType.L2, params: { nlist: '16' } });
console.log('Created IVF_SQ8 index');
