import { MilvusClient, MetricType } from '@zilliz/milvus2-sdk-node';
const client = new MilvusClient({ address: process.env.MILVUS_ADDRESS || '{{milvus_address}}' });
const name = process.env.MILVUS_COLLECTION || '{{collection_name}}';
await client.insert({ collection_name: name, fields_data: [{ text: 'hello world', embedding: [0.1,0.2,0.3] }, { text: 'fluorite', embedding: [0.2,0.1,0.25] }] });
await client.loadCollection({ collection_name: name });
const res = await client.search({ collection_name: name, vector: [[0.1,0.2,0.3]], anns_field: 'embedding', metric_type: MetricType.L2, output_fields: ['text'], limit: 2 });
console.log(res?.results);
