import { MilvusClient } from '@zilliz/milvus2-sdk-node';
const client = new MilvusClient({ address: process.env.MILVUS_ADDRESS || '{{milvus_address}}' });
await client.loadPartitions({ collection_name: process.env.MILVUS_COLLECTION || '{{collection_name}}', partition_names: [process.env.MILVUS_PARTITION || '{{partition_name}}'] });
console.log('Loaded partition');
