const AWS = require('aws-sdk');
const s3 = new AWS.S3({ endpoint: 'http://127.0.0.1:9000', s3ForcePathStyle: true, accessKeyId: process.env.MINIO_ACCESS_KEY||'minioadmin', secretAccessKey: process.env.MINIO_SECRET_KEY||'minioadmin' });
(async () => { const r = await s3.listBuckets().promise(); console.log(r.Buckets?.length); })();
