import { Mistral } from '@mistralai/mistralai';
const client = new Mistral({ apiKey: process.env.MISTRAL_API_KEY });
const res = await client.chat.complete({ model: '{{mistral_model}}', messages: [{ role: 'user', content: 'What time is it? Use the tool.' }], tools: [{ type: 'function', function: { name: 'get_time', description: 'Get current time', parameters: { type: 'object', properties: {} } } }] });
console.log(JSON.stringify(res, null, 2));
