import { GraphQLClient, gql } from 'graphql-request';
const client = new GraphQLClient('https://api.monday.com/v2', { headers: { Authorization: process.env.MONDAY_TOKEN } });
const q = gql`{ me { name } }`;
try { const res = await client.request(q); console.log(!!res.me.name); } catch(e){ console.log('auth required'); }
