import * as React from 'react';
import { DataGrid, GridColDef } from '@mui/x-data-grid';
const rows = [{ id: 1, col1: 'Hello', col2: 'World' }];
const columns: GridColDef[] = [{ field: 'col1', headerName: 'Col1', width: 150 }, { field: 'col2', headerName: 'Col2', width: 150 }];
export default function Grid(){ return <div style={{ height: 300, width: 320 }}><DataGrid rows={rows} columns={columns} /></div>; }
