import * as React from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button } from '@mui/material';
export default function Dx(){ const [o,setO]=React.useState(false); return (<div><Button variant='outlined' onClick={()=>setO(true)}>Open</Button><Dialog open={o} onClose={()=>setO(false)}><DialogTitle>Title</DialogTitle><DialogContent>Body</DialogContent><DialogActions><Button onClick={()=>setO(false)}>Close</Button></DialogActions></Dialog></div>); }
