import * as React from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
export default function MuiForm(){ const [v,setV]=React.useState(''); const [err,setErr]=React.useState(''); return (<form onSubmit={e=>{e.preventDefault(); setErr(v.includes('@')?'':'Please enter an email');}}><TextField value={v} onChange={e=>setV(e.target.value)} label='Email' error={!!err} helperText={err||' '} /><Button type='submit'>Submit</Button></form>); }
