import * as React from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useForm } from 'react-hook-form';
export function MuiLogin(){ const { register, handleSubmit } = useForm(); return (<form onSubmit={handleSubmit(console.log)}><TextField label='Email' {...register('email')} /><TextField label='Password' type='password' {...register('password')} /><Button type='submit' variant='contained'>Login</Button></form>); }
