import * as React from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
const schema = z.object({ email: z.string().email('Invalid email') });
export default function Form(){ const { register, handleSubmit, formState:{ errors } } = useForm({ resolver: zodResolver(schema) }); return (<form onSubmit={handleSubmit(console.log)}><TextField label='Email' {...register('email')} error={!!errors.email} helperText={errors.email?.message as any} /><Button type='submit'>Send</Button></form>); }
