import * as React from 'react';
import Snackbar from '@mui/material/Snackbar';
import Button from '@mui/material/Button';
export default function Snack(){ const [o,setO]=React.useState(false); return (<div><Button onClick={()=>setO(true)}>Show</Button><Snackbar open={o} autoHideDuration={2000} onClose={()=>setO(false)} message='Saved' /></div>); }
