import * as React from 'react';
import { Stepper, Step, StepLabel, Button } from '@mui/material';
const steps = ['One','Two','Three'];
export default function StepperExample(){ const [active, setActive] = React.useState(0); return (<div><Stepper activeStep={active}>{steps.map(s=>(<Step key={s}><StepLabel>{s}</StepLabel></Step>))}</Stepper><div className='mt-2'><Button disabled={active===0} onClick={()=>setActive(a=>a-1)}>Back</Button><Button disabled={active===steps.length-1} onClick={()=>setActive(a=>a+1)}>Next</Button></div></div>); }
