import * as React from 'react';
import { Tabs, Tab, Box } from '@mui/material';
export default function TabsExample(){ const [v,setV]=React.useState(0); return (<Box><Tabs value={v} onChange={(_,nv)=>setV(nv)}><Tab label='A'/><Tab label='B'/></Tabs><Box mt={2}>{v===0?'Panel A':'Panel B'}</Box></Box>); }
