import * as React from 'react';
import { createTheme, ThemeProvider, Button } from '@mui/material';
export default function ThemePersist(){ const [dark,setDark]=React.useState(false); React.useEffect(()=>{ setDark(localStorage.getItem('theme')==='dark'); },[]); React.useEffect(()=>{ localStorage.setItem('theme', dark?'dark':'light'); },[dark]); const theme = React.useMemo(()=>createTheme({ palette:{ mode: dark?'dark':'light' } }), [dark]); return (<ThemeProvider theme={theme}><Button onClick={()=>setDark(d=>!d)}>{dark?'Dark':'Light'}</Button></ThemeProvider>); }
