import * as React from 'react';
import { createTheme, ThemeProvider, Button } from '@mui/material';
export default function ThemeToggle(){ const [dark,setDark]=React.useState(false); const theme = React.useMemo(()=>createTheme({ palette:{ mode: dark?'dark':'light' } }), [dark]); return (<ThemeProvider theme={theme}><Button variant='contained' onClick={()=>setDark(d=>!d)}>{dark?'Dark':'Light'}</Button></ThemeProvider>); }
